'use strict';

/**
 * @module UTF16
 * @author nuintun
 */
/**
 * @function encode
 * @param {string} text
 * @returns {number[]}
 */
function encode(text) {
  var length = text.length;
  var bytes = [];
  for (var i = 0; i < length; i++) {
    bytes.push(text.charCodeAt(i));
  }
  return bytes;
}

exports.encode = encode;
